import '@alkaswap/core/contracts/interfaces/IERC20Minimal.sol';
import '@alkaswap/core/contracts/interfaces/IAlgebraPool.sol';

/// @param rewardToken The token being distributed as a reward
/// @param bonusRewardToken The bonus token being distributed as a reward
/// @param pool The Algebra pool
/// @param nonce The nonce of incentive
struct IncentiveKey {
  IERC20Minimal rewardToken;
  IERC20Minimal bonusRewardToken;
  IAlgebraPool pool;
